import React, { useState, useRef } from "react";
import emailjs from "@emailjs/browser";
import "../css/contact.css";
// npm i @emailjs/browser

const Contact = () => {
    const form = useRef();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [status, setStatus] = useState("");

    const handleNameChange = (e) => {
        setName(e.target.value);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handleMessageChange = (e) => {
        setMessage(e.target.value);
    };

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm(
                "service_4ik89zv",
                "template_rkgcqoo",
                form.current,
                "Ac1RL4TgJZVZgpMSY"
            )
            .then(
                (result) => {
                    console.log(result.text);
                    console.log("message sent");
                    alert("your message has been sent.");
                    setName("");
                    setEmail("");
                    setMessage("");
                },
                (error) => {
                    console.log(error.text);
                    setStatus("Error sending message.");
                }
            );
    };
const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    const url = "http://localhost:5000/api/users";
    const { data: res } = await axios.post(url, data);

    // send welcome email to user
    emailjs
      .send("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", {
        to_email: data.email,
      }, "YOUR_USER_ID")
      .then((result) => {
        console.log(result.text);
      }, (error) => {
        console.log(error.text);
      });

    navigate("/login");
    console.log(res.message);
  } catch (error) {
    if (
      error.response &&
      error.response.status >= 400 &&
      error.response.status <= 500
    ) {
      setError(error.response.data.message);
    }
  }
};

    return (
        <form className="contantForm" ref={form} onSubmit={sendEmail}>
            <h1>Contact Us</h1>
            <p>
                If you have any questions or comments, please fill out the form below and
            </p>
            <p>
                we will get back to you as soon as possible.
            </p>
            Name:
            <label>

                <input type="text" name="name" value={name} onChange={handleNameChange} className="contact-input" />
            </label>
            Email:
            <label>

                <input type="email" name="email" value={email} onChange={handleEmailChange} className="contact-input" />
            </label>
            Message:
            <label>

                <textarea name="message" value={message} onChange={handleMessageChange} className="contact-textarea" />
            </label>
            <button type="submit" className="contact-button">Send</button>
            {status && <div>{status}</div>}
        </form>

    );
};

export default Contact;
