import styles from "./styles.module.css";
import { Link} from 'react-router-dom';
const Main = () => {
	const user = localStorage.getItem('token');

	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();


	};


	return (
		<div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>fakebook</h1>
        {user && <Link to="/" className={styles.white_btn} onClick={handleLogout}>Logout</Link>}
        {!user && <Link to="/signup" className={styles.white_btn}>Sign Up</Link>}
        {!user && <Link to="/login" className={styles.white_btn}>Log In</Link>}
      </nav>
    </div>
	);
};

export default Main;
